import React, { useState } from 'react';

const BingSearch = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  const subscriptionKey = 'cdca5c31804a4a0cb18bb57cddb79ddd';
  const endpoint = 'https://api.bing.microsoft.com/v7.0/search';

  const handleSearch = async () => {
    const response = await fetch(`${endpoint}?q=${encodeURIComponent(searchTerm)}`, {
      headers: {
        'Ocp-Apim-Subscription-Key': subscriptionKey
      }
    });

    const data = await response.json();
    setSearchResults(data.webPages.value);
  };

  return (
    <div>
      <input type="text" value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} />
      <button onClick={handleSearch}>Search</button>
      <ul>
        {searchResults.map((result) => (
          <li key={result.id}><a href={result.url}>{result.name}</a></li>
        ))}
      </ul>
    </div>
  );
};

export default BingSearch;
